import React, { useEffect } from 'react';
import * as d3 from 'd3';

function GitGraph({ commits, onSelectCommit }) {
    useEffect(() => {
        if (commits.length === 0) return;

        // Clear existing SVG (if any) before drawing
        d3.select("#git-graph").html("");

        const circleRadius = 15;
        const circleSpacing = 50;
        const svgWidth = 800;
        const svgHeight = commits.length * circleSpacing + circleRadius * 2;

        const svg = d3.select("#git-graph")
            .append("svg")
            .attr("width", svgWidth)
            .attr("height", svgHeight);

        // Lines connecting commits
        svg.selectAll("line")
            .data(commits.slice(1))  // No line for the first commit
            .enter()
            .append("line")
            .attr("x1", svgWidth / 2)
            .attr("y1", (d, i) => i * circleSpacing + circleRadius)
            .attr("x2", svgWidth / 2)
            .attr("y2", (d, i) => (i + 1) * circleSpacing)
            .attr("stroke", "#999")
            .attr("stroke-width", 2);

        // Circles representing commits
        svg.selectAll("circle")
            .data(commits)
            .enter()
            .append("circle")
            .attr("cx", svgWidth / 2)
            .attr("cy", (d, i) => i * circleSpacing + circleRadius)
            .attr("r", circleRadius)
            .attr("fill", "#3498db")
            .attr("stroke", "#2980b9")
            .attr("stroke-width", 3)
            .on("mouseover", function() {
                d3.select(this).attr("fill", "#e74c3c");
            })
            .on("mouseout", function() {
                d3.select(this).attr("fill", "#3498db");
            })
            .on("click", (event, d) => {
                onSelectCommit(d);
            });

        // Commit tooltips
        const tooltip = d3.select("body")
            .append("div")
            .attr("class", "tooltip")
            .style("opacity", 0)
            .style("background-color", "#333")
            .style("color", "#fff")
            .style("padding", "5px")
            .style("border-radius", "5px")
            .style("position", "absolute");

        svg.selectAll("circle")
            .on("mouseover", function(event, d) {
                tooltip.transition().duration(200).style("opacity", .9);
                tooltip.html(d.commit.message)
                    .style("left", (event.pageX + 5) + "px")
                    .style("top", (event.pageY - 28) + "px");
            })
            .on("mouseout", function() {
                tooltip.transition().duration(500).style("opacity", 0);
            });

    }, [commits, onSelectCommit]);

    return (
        <div id="git-graph"></div>
    );
}

export default GitGraph;
