import React, { useEffect } from 'react';
import * as d3 from 'd3';

function GitGraph({ commits, onSelectCommit }) {
    useEffect(() => {
        if (commits.length === 0) return;

        // Basic example: Display commits as circles
        const svg = d3.select("#git-graph")
            .append("svg")
            .attr("width", 800)
            .attr("height", commits.length * 30);  // Simple vertical layout

            svg.selectAll("circle")
            .data(commits)
            .enter()
            .append("circle")
            .attr("cx", 50)
            .attr("cy", (d, i) => i * 30)
            .attr("r", 10)
            .on("click", (event, d) => {
                // Here, 'd' is the data of the clicked circle (commit)
                onSelectCommit(d);  // Call a prop function with the selected commit data
            });

        // You can add more details, interactivity, lines connecting commits, etc.
    }, [commits]);

    return (
        <div id="git-graph"></div>
    );
}

export default GitGraph;
